function append_row(n,a){var e="<tr>";e+="<td>"+a.label+'</td><td><a href="'+a.source+'">'+a.by+"</a></td>",e+="</tr>",n.append(e)}function append_aka(n){$.ajax({url:"/data/family.json?"+Math.random(),cache:!1,dataType:"text",success:function(a){for(a=JSON.parse(a),nodes=a.nodes,i=0;i<nodes.length;i++)nodes[i].connect==n&&(append_row($("#aka"),nodes[i]),console.log(nodes[i].label))},error:function(n){console.log("ERROR",n)}})}